// generated by polka.codes

export function getImplementPrompt(plan: string): string {
  return `Implement the following plan:

${plan}

Please implement all the necessary code changes according to this plan.`
}

export function getFixPrompt(command: string, exitCode: number, stdout: string, stderr: string): string {
  return `The command "${command}" failed with exit code ${exitCode}.
Please fix the code to resolve the errors.

stdout:
${stdout}

stderr:
${stderr}
`
}
